import React from "react";
import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
//import StatesJson from "../../config/state.json";
import Layout from "../../layout/Layout";

function CoursePage({
  show,
  handleClose,
  handleShow,
  handleChange,
  user,
  handleSubmit,
  courseState,
  edit,
}) {
  //console.log(StatesJson, "gggg");
  const { title, state } = user;
  return (
    <div>
      <Layout>
        <Button variant="primary" onClick={handleShow}>
          Add Course
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={handleSubmit}>
              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlInput1"
              >
                <Form.Label>Course State</Form.Label>
                <Form.Control
                  as="select"
                  placeholder="name@example.com"
                  name="state"
                  //value={state}
                  value={(courseData.state, addNewCourse.state)}
                  onChange={handleChange}
                  autoFocus
                >
                  <option>select course state</option>
                  {courseState &&
                    courseState.length > 0 &&
                    courseState.map((state) => (
                      <option key={state.abbreviation}>{state.name}</option>
                    ))}
                </Form.Control>
              </Form.Group>
              <Form.Group
                className="mb-3"
                controlId="exampleForm.ControlTextarea1"
              >
                <Form.Label>Course Title</Form.Label>
                <Form.Control
                  as="textarea"
                  type="text"
                  name="title"
                  value={title}
                  onChange={handleChange}
                />
              </Form.Group>
              <Button type="submit">Submit</Button>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>

            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </Layout>
    </div>
  );
}

export default CoursePage;
