import React, { useEffect, useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { addNewCourse } from "../../services/copyCourseServices";
import state from "../../config/state.json";

const NewAddModule = () => {
  const [json, setJson] = useState([]);
  const [show, setShow] = useState(false);
  const [user, setUser] = useState({});
  useEffect(() => {
    setJson(state);
  });
  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };
  const handleSubmit = async () => {
    const res = await addNewCourse(user);
    console.log(res, "ppppppppp");
  };

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Course Model</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>course State</Form.Label>
              <Form.Control
                type="text"
                placeholder="name@example.com"
                name="state"
                value={user.state}
                onChange={handleChange}
                autoFocus
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>course Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="name@example.com"
                name="title"
                value={user.title}
                onChange={handleChange}
                autoFocus
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label> Module Title </Form.Label>
              <Form.Control
                type="text"
                placeholder="name@example.com"
                name="courseId"
                value={user.controlId}
                onChange={handleChange}
                autoFocus
              />
            </Form.Group>

            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Order Number</Form.Label>
              <Form.Control
                type="number"
                placeholder="name@example.com"
                name="number"
                value={user.number}
                onChange={handleChange}
                autoFocus
              />
            </Form.Group>
          </Form>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="success" onClick={handleSubmit}>
            Add Course
          </Button>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
export default NewAddModule;
