import Layout from "../layout/Layout";
import NewAddModule from "../models/courseModulePage/NewAddModule";
import { Button, Table } from "react-bootstrap";
import {
  getCourseData,
  addNewCourse,
  deleteCourseModule,
} from "../services/CopyCourseModule";
import { useEffect, useState } from "react";
import state from "../config/state.json";

const ModulePage = () => {
  console.log(state, "lllllllllll");
  const [json, setJson] = useState([]);
  const [show, setShow] = useState(false);
  const [user, setUser] = useState({});
  const [userData, setUserData] = useState([]);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    setJson(state.states);
  }, []);
  useEffect(() => {
    getAllData();
  }, []);

  console.log(user, "klmnop");
  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    handleClose();
    const res = await addNewCourse(user);
    console.log(res, "ppppppppp");
    getAllData();
  };

  const getAllData = async () => {
    const res = await getCourseData();
    console.log(res, "dddddddddddd");
    if (res && res.status === 200) {
      setUserData(res.data.data);
    }
  };
  console.log(user, "jkjkj");

  const handledelete = async (id) => {
    console.log(id, "ppp");
    const res = await deleteCourseModule(id);
    console.log(res, "pppppppppppplllllllllll");
    getAllData();
  };
  return (
    <>
      <Layout>
        <NewAddModule
          user={user}
          handleSubmit={handleSubmit}
          handleChange={handleChange}
          handleClose={handleClose}
          handleShow={handleShow}
          show={show}
          json={json}
        />
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>State</th>
              <th>Course Title</th>
              {/* <th>Module Title</th> */}
              <th>OrderNo</th>
            </tr>
          </thead>
          {userData &&
            userData.map((item) => (
              <tbody>
                <tr>
                  <td>{item.state}</td>
                  <td>{item.title}</td>
                  {/* <td>{item.courseId}</td> */}
                  <td>{item.orderNo}</td>
                  <td>
                    <Button type="submit" onClick={() => handledelete(item.id)}>
                      Delete
                    </Button>
                  </td>
                </tr>
              </tbody>
            ))}
        </Table>
      </Layout>
    </>
  );
};
export default ModulePage;
