import React, { useEffect, useState } from "react";

import CoursePage from "./CoursePage";
import {
  addCourseApi,
  getCourseApi,
  editCourseApi,
} from "../../services/copyCourse";
import StatesJson from "../../config/state.json";
import CourseTable from "./CourseTable";
import Layout from "../../layout/Layout";
import EditCoursePage from "./EditCoursePage";

const CoursePageParent = () => {
  useEffect(() => {
    getAllData();
  }, []);
  console.log(StatesJson, "LLLLLLLLLLLLL");
  const [userData, setUserData] = useState([]);
  const [show, setShow] = useState(false);
  const [id, setId] = useState("");
  const [courseState, setCourseState] = useState([]);
  console.log(courseState, "iiiiiiiiiiiiii");
  const [user, setUser] = useState({
    state: "",
    title: "",
  });
  useEffect(() => {
    setCourseState(StatesJson.states);
  }, []);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAdd = () => {
    handleShow();
    setUser({});
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };
  console.log(user, "ppppppppppppppp");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await addCourseApi(user);
    console.log(res, "llllllllllll");
    getAllData();
    handleClose();
  };

  const getAllData = async () => {
    const res = await getCourseApi();
    console.log(res, "dddddddddddd");
    if (res && res.status === 200) {
      setUserData(res.data.data);
    }
  };
  const handleEdit = (item, e) => {
    handleShow();

    setId(item.id);
    setUser({ ...user, [e.target.name]: e.target.value });
    console.log(item, "nnnnnnnnnnn");
    getAllData();
  };

  const handleUpdate = async () => {
    const res = await editCourseApi(user, id);
    console.log(res, "ooooo");
  };

  return (
    <>
      <Layout>
        <CoursePage
          handleClose={handleClose}
          handleShow={handleShow}
          show={show}
          handleChange={handleChange}
          user={user}
          handleSubmit={handleSubmit}
          courseState={courseState}
          handleAdd={handleAdd}
          handleUpdate={handleUpdate}
        />
        <CourseTable userData={userData} handleEdit={handleEdit} />
        {/* <EditCoursePage
          // handleClose={handleClose}
          handleShow={handleShow}
          show={show}
        /> */}
      </Layout>
    </>
  );
};

export default CoursePageParent;
