import axios from "axios";
import { apiBaseUrl } from "../contants/constants";

const token = localStorage.getItem("admin_token");
const headerData = { Authorization: `Bearer ${token}` };
export const addCourseApi = async (data) => {
  console.log(data, "datata");
  try {
    const result = await axios.post(`${apiBaseUrl}/admin/course/create`, data, {
      headers: headerData,
    });
    console.log(result, "ddd");
    if (response.status === 201) {
      return {
        data: response.data,
      };
    } else {
      throw new Error(response.message);
    }
  } catch (err) {
    return {
      status: 400,
      message: err.message,
      data: "",
    };
  }
};
export const getCourseApi = async () => {
  try {
    const result = await axios.get(`${apiBaseUrl}/admin/course/getAll`, {
      headers: headerData,
    });
    if (result) {
      return result;
    }
  } catch (err) {
    return { data: err.response.data };
  }
};
export const editCourseApi = async (data, id) => {
  try {
    const result = await axios.put(
      `${apiBaseUrl}/admin/course/edit/${id}`,
      data,
      {
        headers: headerData,
      }
    );
    if (result) {
      return result;
    }
  } catch (err) {
    return { data: err.response.data };
  }
};
