  //   console.log(item, "nnnnnnnnnnn");
  //   getAllData();
  // };

  // const handleUpdate = async () => {
  //   const res = await editCourseApi(user, id);
  //   console.log(res, "ooooo");
  // };
  const handleEdit = (data) => {
    handleShow();
    setEdit(true);
    setUser(data);
  };
  const handleUpdate = async (e) => {
    e.preventDefault();
    setEdit(true);
    console.log(user.id, "fffffffffffffff");

    const res = await getEditCourseApi(user.id, user);
    console.log(res, "fffffffff");

    getAllData();
    setShow(false);

    getAllData();
  };

  const handledelete = async (id) => {
    console.log(id, "ppp");
    const res = await deleteCourseApi(id);
    console.log(res, "pppppppppppplllllllllll");
    getAllData();
  };
  // const handleSreach = () => {
  //   const res = await SearchCourseApi()
  // };
  const handleSearch = async (e) => {
    setSearchInp(e.target.value);
    const text = e.target.value;
    const response = await SearchCourseApi(activePage, pageLimit, text);
    console.log(response, "poojamaurya");
    setUserData(response.data.data);
  };

  return (
    <>
      <Layout>
        <CoursePage
          handleClose={handleClose}
          handleShow={handleShow}
          show={show}
          handleChange={handleChange}
          user={user}
          edit={edit}
          handleSubmit={handleSubmit}
          courseState={courseState}
          handleUpdate={handleUpdate}
        />
        <CourseTable
          userData={userData}
          handleEdit={handleEdit}
          handleAdd={handleAdd}
          handledelete={handledelete}
        />
      </Layout>
    </>
  );
};

export default CoursePageParent;
