export const addCourseApi = async (data) => {
  try {
    const result = await axios.post(
      `${apiBaseUrl}/admin/course/create`,

      data,
      {
        headers: headerData,
      }
    );
    if (result) {
      return result;
    }
  } catch (err) {
    return { data: err.response.data };
  }
};

export const getCourseApi = async () => {
  try {
    const result = await axios.get(`${apiBaseUrl}/admin/course/getAll`, {
      headers: headerData,
    });
    if (result) {
      return result;
    }
  } catch (err) {
    return { data: err.response.data };
  }
};
export const getEditCourseApi = async (id, data) => {
  try {
    const result = await axios.put(
      `${apiBaseUrl}/admin/course/edit/${id}`,
      data,
      {
        headers: headerData,
      }
    );
    if (result) {
      return result;
    }
  } catch (err) {
    return { data: err.response.data };
  }
};
